var TxtType = function(el, toRotate, period) {
    this.toRotate = toRotate;
    this.el = el;
    this.loopNum = 0;
    this.period = parseInt(period, 10) || 2000;
    this.txt = '';
    this.tick();
    this.isDeleting = false;
};

TxtType.prototype.tick = function() {
    var i = this.loopNum % this.toRotate.length;
    var fullTxt = this.toRotate[i];

    if (this.isDeleting) {
        this.txt = fullTxt.substring(0, this.txt.length - 1);
    } else {
        this.txt = fullTxt.substring(0, this.txt.length + 1);
    }

    this.el.innerHTML = '<span class="wrap">' + this.txt + '</span>';

    var that = this;
    var delta = 200 - Math.random() * 100;

    if (this.isDeleting) {
        delta /= 2;
    }

    if (!this.isDeleting && this.txt === fullTxt) {
        delta = this.period;
        this.isDeleting = true;
    } else if (this.isDeleting && this.txt === '') {
        this.isDeleting = false;
        this.loopNum++;
        delta = 500;
    }

    setTimeout(function() {
        that.tick();
    }, delta);
};

window.onload = function() {
    var elements = document.getElementsByClassName('typewrite');
    for (var i = 0; i < elements.length; i++) {
        var toRotate = elements[i].getAttribute('data-type');
        var period = elements[i].getAttribute('data-period');
        if (toRotate) {
            new TxtType(elements[i], JSON.parse(toRotate), period);
        }
    }
    // INJECT CSS
    var css = document.createElement("style");
    css.type = "text/css";
    css.innerHTML = ".typewrite > .wrap { border-right: 0.08em solid #2ecc71}";
    document.body.appendChild(css);
};
var resume = document.getElementsByClassName('square')[0]
var skill = document.getElementsByClassName('square')[1]
var project = document.getElementsByClassName('square')[2]
var close = document.getElementsByClassName('icon')[0]

resume.addEventListener('mouseover', function() {
    resume.classList.add('hvr-shutter-in-vertical');
})
skill.addEventListener('mouseover', function() {
    skill.classList.add('hvr-sweep-to-left');

})
project.addEventListener('mouseover', function() {
    project.classList.add('hvr-bounce-to-bottom');
})

resume.addEventListener('click', function() {
    document.getElementsByClassName('modal')[0].classList.add('is-active');
})
skill.addEventListener('click', function() {
    skill.classList.add('hvr-sweep-to-left');
})
project.addEventListener('click', function() {
    project.classList.add('hvr-bounce-to-bottom');
})

close.addEventListener('click', function() {
    var getClose = document.getElementsByClassName('resume-info')
    for (let i = 0; i < getClose.length; i++) {
        getClose[i].style.display = 'none';

    }
    close.style.display = 'none';

})

function dislayProject() {
    document.getElementsByClassName('aproject')[0].style.display = 'block';
    close.style.display = "block";
}

function dislaySkill() {
    document.getElementsByClassName('askill')[0].style.display = 'block';
    close.style.display = "block";
}

function dislayResume() {
    document.getElementsByClassName('aresume')[0].style.display = 'block';
    close.style.display = "block";
}